CREATE TABLE IF NOT EXISTS INT_MESSAGE (MESSAGE_ID CHAR(36) NOT NULL PRIMARY KEY,	REGION VARCHAR(100), CREATED_DATE TIMESTAMP NOT NULL, MESSAGE_BYTES BYTEA, IDENTIFIER CHAR(50));
CREATE TABLE IF NOT EXISTS INT_MESSAGE_GROUP (MESSAGE_ID CHAR(36) NOT NULL, GROUP_KEY CHAR(36) NOT NULL, REGION VARCHAR(100),	MARKED BIGINT, COMPLETE BIGINT,	LAST_RELEASED_SEQUENCE BIGINT, CREATED_DATE TIMESTAMP NOT NULL, UPDATED_DATE TIMESTAMP DEFAULT NULL, MESSAGE_BYTES BYTEA, constraint MESSAGE_GROUP_PK primary key (MESSAGE_ID, GROUP_KEY));